import {
  Component,
  Input,
  AfterViewInit,
  ViewChild,
  OnInit,
} from '@angular/core';
import { Product } from '../product';
import { MatTableDataSource } from '@angular/material/table';
import { MatSort } from '@angular/material/sort';
import { Observable } from 'rxjs';
import { MatPaginator } from '@angular/material/paginator';

@Component({
  selector: 'app-product-table',
  templateUrl: './product-table.component.html',
  styleUrls: ['./product-table.component.css'],
})
export class ProductTableComponent implements OnInit {
  @Input()
  set products$(products$: Observable<Product[]>) {
    this._products$ = products$;
    this._products$.subscribe((products) => {
      this.dataSource.data = products;
      if (this.paginator_active) {
        this.dataSource.paginator = this.paginator;
      }
      if (this.sort_active) {
        this.dataSource.sort = this.sort;
      }
    });
  }

  @Input() sort_active = false;
  @Input() paginator_active = false;

  @ViewChild(MatSort) sort: MatSort | null = null;
  @ViewChild(MatPaginator) paginator: MatPaginator | null = null;
  public dataSource = new MatTableDataSource<Product>();
  public columnNames = ['name', 'price', 'actions'];

  private _products$!: Observable<Product[]>;

  get products$(): Observable<Product[]> {
    return this._products$;
  }

  ngOnInit(): void {
    // Set the dataSource sort to the ViewChild MatSort
    if (this.paginator_active) {
      this.dataSource.paginator = this.paginator;
    }
    else {
      this.dataSource.paginator = null;
    }
    if (this.sort_active) {
      this.dataSource.sort = this.sort;
    }
    else {
      this.dataSource.sort = null
    }
  }
}

import { Component, Input, OnInit, OnDestroy } from '@angular/core';
import { ProductViewService } from './product-view.service';
import { Subject, takeUntil } from 'rxjs';

@Component({
  selector: 'app-product-view',
  templateUrl: './product-view.component.html',
  styleUrls: ['./product-view.component.css'],
  providers: [ProductViewService],
})
export class ProductViewComponent implements OnInit, OnDestroy {
  @Input()
  id = -1;

  name: string = '';
  price: number = 0;

  private productSub = new Subject<void>();

  constructor(private productviewService: ProductViewService) {}

  /** Services */
  private getProduct() {
    this.productviewService
      .getProduct(this.id)
      .pipe(takeUntil(this.productSub))
      .subscribe((product) => {
        if (product) {
          this.name = product.name;
          this.price = product.price;
        }
      });
  }

  /** Component Lifecycle */
  ngOnInit(): void {
    this.getProduct();
  }

  ngOnDestroy(): void {
    this.productSub.next();
    this.productSub.complete();
  }
}

